import React, { useState, useEffect } from "react";


function ProfileStudent() {
    const [values, setValues] = useState({    //เก็บค่าตัวแปรต่างๆ
        name: "",
        nickname:"",
        email: "",
        phone: "",
        address: "",
        college: "",
        faculty: "",
        program: "",
        transcript: "",
        img: null,

      });
      const handleChange = (e) => {   //การเปลี่ยนแปรงค่าเมื่อกรอก
         setValues({ ...values, 
           [e.target.name]: e.target.value });
       };
       const handleSubmit = (e) => {
    //     console.log("proIm isL",values.img)
    //     if (values.img === null || values.img === '' ){
    //         alert('please upload profile image')
    //     }
    //     else{
    //     alert("Saved");
    //     e.preventDefault();
    //     setValues({
    //       img:values.img,

    //     });
     }
    return (

        <div className="font-sans my-20 mx-24 text-black ">
            <div className=" font-bold text-6xl ">
            Profile
            </div>
            <form onSubmit={handleSubmit}>

                <div className="my-10 flex flex-row ml-auto space-x-20">
                    <div>
                        <label className="block text-gray-700 text-m font-bold mb-2" // ชื่อ-นามสกุล
                        >       
                            ชื่อ-นามสกุล *
                        </label> 
                        <input className="shadow appearance-none border rounded w-96 h-12 py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"    
                        id="name"
                        type="name"
                        onChange={handleChange}
                        name="name"
                        value={values.name}
                        required
                        autoComplete="none"
                        />
                    </div>
                    <div>
                        <label className="block text-gray-700 text-m font-bold mb-2" // ชื่อ-นามสกุล
                        >       
                            ชื่อเล่น *
                        </label> 
                        <input className="shadow appearance-none border rounded w-96 h-12  py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"    
                        id="nickname"
                        type="nickname"
                        onChange={handleChange}
                        name="nickname"
                        value={values.nickname}
                        required
                        autoComplete="none"
                        />
                    </div>


                </div>

            </form>
      </div>

    );
}
export default ProfileStudent;
