import React, { useRef } from 'react';
import emailjs from '@emailjs/browser';

function Contact ()  {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', form.current, 'YOUR_PUBLIC_KEY')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
  };

  return (
    <form >
        <div className="font-body my-20 mx-24 text-black ">

            <div className=" font-bold text-6xl">
            ติดต่อสอบถาม
            </div>
                <div className='px-20 my-10'>
                    <div>
                        <label className=" block text-gray-700 text-m font-bold mb-2 mt-10" // ชื่อ-นามสกุล
                            >       
                                ชื่อ-นามสกุล *
                        </label> 
                        <input className="shadow appearance-none border rounded w-11/12 h-12 py-2 px-3  text-gray-700 leading-tight focus:outline-none border-gray-50 focus:border-primary-80 focus:shadow-outline"    
                            id="name"
                            type="name"
                            name="name"
                            required
                            autoComplete="none"
                        />
                    </div>   

                    <div>
                        <label className="block text-gray-700 text-m font-bold mb-2 mt-8" // E-mail
                        >       
                            E-mail *
                        </label> 
                        <input className="shadow appearance-none border rounded w-96 h-12 py-2 px-3 text-gray-700 leading-tight focus:outline-none border-gray-50 focus:border-primary-80 focus:shadow-outline"    
                            id="email"
                            type="email"
                            name="email"
                            required
                            autoComplete="none"
                        />
                    </div>
                </div>

            </div>
    </form>
  );
}
export default Contact;
