import React, { useState, useEffect } from "react";


function ProfileStudent() {
    const [values, setValues] = useState({
        name: "",
        email: "",
        phone: "",
        address: "",
        college: "",
        faculty: "",
        program: "",
        transcript: "",
        img: null,

      });
      const handleChange = (e) => {
        setValues({ ...values, 
          [e.target.name]: e.target.value });
      };
    return (

        <div className="font-sans my-20 mx-24 text-black ">
            <div className=" font-bold text-5xl ">
            Profile
            </div>
            <div className="my-10 ">
                <label className="block text-gray-700 text-sm font-bold mb-2" // ชื่อ-นามสกุล
                >       
                    ชื่อ-นามสกุล *
                </label> 
                <input className=""    
                id="name"
                type="name"
                onChange={handleChange}
                name="name"
                value={values.name}
                required
                autoComplete="none"
                  />
          </div>
      </div>

    );
}
export default ProfileStudent;
