        <h1 className="font-bold text-2xl leading-8 md:text-3xl font-body">
          ใบสมัคร
        </h1>
      </div>

      {/* Map data */}
      {values.map((value, index) => (
        <CardApply
          key={values.id}
          index={index}
          student={value.student}
          college={value.college}
          subject={value.subject}
          type={value.type}
          wage={value.wage}
          place={value.place}
        />
      ))}
    </div>
  );
};

export default DetailCard;
